import styled from 'styled-components';
import Globals from './Globals';
import Images from './Images';
const { colors, fontSizes } = Globals;

const Heading = styled.h3`
  position: relative;
  display: flex;
  align-items: center;
  margin: 10px 0 40px;
  width: 100%;
  white-space: nowrap;
  font-size: ${fontSizes.h3};
  ${Images.tablet`font-size: 24px;`};
  &:after {
    content: '';
    display: block;
    height: 1px;
    width: 300px;
    background-color: ${colors.blue};
    position: relative;
    margin-left: 20px;
    ${Images.desktop`width: 200px`};
    ${Images.tablet`width: 100%;`};
    ${Images.thone`margin-left: 10px;`};
  }
`;

export default Heading;
