import React, { Component } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import resume from '../content/active_bradyFox_resume.pdf';
import styled from 'styled-components';
import { Globals, Images, Navigation, Button } from '../styles';
import { IconLogo } from '../images/icons';
const { colors, fontSizes, fonts } = Globals;

const HeaderContainer = styled.header`
  // display: flex;
  // justify-content: space-between;
  // align-items: center;
  position: fixed;
  top: 0;
  padding: 0px 30px;
  background-color: ${colors.white};
  transition: ${Globals.transition};
  z-index: 11;
  pointer-events: auto !important;
  user-select: auto !important;
  width: 100%;
  height: ${Globals.headerHeight};
  ${Images.desktop`padding: 0 40px;`};
  ${Images.tablet`padding: 0 25px;`};
`;
const Navbar = styled(Navigation)`
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-family: ${fonts.Raleway};
  color: ${colors.blue};
  position: relative;
  z-index: 12;
`;

const Logo = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const NavLinks = styled.div`
  display: flex;
  align-items: center;
  ${Images.tablet`display: none;`};
`;

const ResumeLink = styled(Button)`
  margin-left: 10px;
  right: 10;
  color: ${colors.blue};
  background-color: ${colors.white};
  font-size: ${fontSizes.smallish};
`;

class Header extends Component {
  render() {
    return (
      <HeaderContainer>
        <Navbar>
          <NavLinks>
            <TransitionGroup>
              {
                <CSSTransition classNames="fade" timeout={3000}>
                  <Logo>
                    <IconLogo />
                  </Logo>
                </CSSTransition>
              }
            </TransitionGroup>
            <TransitionGroup>
              {
                <CSSTransition classNames="fadedown" timeout={3000}>
                  <div style={{ transitionDelay: `600ms` }}>
                    <ResumeLink
                      href={resume}
                      target="_blank"
                      rel="nofollow noopener noreferrer"
                    >
                      My Resume
                    </ResumeLink>
                  </div> 
                </CSSTransition>
              }
            </TransitionGroup>
          </NavLinks>
        </Navbar>
      </HeaderContainer>
    );
  }
}

export default Header;
