import React, { Component } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { email } from '../meta';
import styled from 'styled-components';
import { Globals, Misc, Images, Section, Button } from '../styles';
const { colors, fontSizes, fonts } = Globals;

const HeroContainer = styled(Section)`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  align-items: flex-start;
  min-height: 100vh;
  ${Images.tablet`padding-top: 150px;`};
  div {
    width: 100%;
  }
`;
const Howdy = styled.h1`
  color: ${colors.blue};
  margin: 0 0 20px 3px;
  font-size: ${fontSizes.xxlarge};
  font-family: ${fonts.Raleway};
  font-weight: normal;
  ${Images.desktop`font-size: ${fontSizes.xlarge};`};
  ${Images.tablet`font-size: ${fontSizes.small};`};
`;
const HeroText = styled.h2`
  font-size: 70px;
  line-height: 1.1;
  margin: 0;
  ${Images.desktop`font-size: 70px;`};
  ${Images.tablet`font-size: 60px;`};
  ${Images.phablet`font-size: 50px;`};
  ${Images.phone`font-size: 40px;`};
`;
const Subtitle = styled.h3`
  font-size: 58px;
  line-height: 1.1;
  color: ${colors.dark};
  padding-top: 10px;
  ${Images.desktop`font-size: 58px;`};
  ${Images.tablet`font-size: 52px;`};
  ${Images.phablet`font-size: 40px;`};
  ${Images.phone`font-size: 32px;`};
`;
const Blurb = styled.div`
  margin-top: 25px;
  width: 50%;
  max-width: 500px;
  a {
    ${Misc.inlineLink};
  }
`;
const HeroButton = styled(Button)`
  font-size: ${fontSizes.smallish};
  margin-top: 50px;
`;

class Hero extends Component {
  state = {
    isMounted: false
  };

  componentDidMount() {
    setTimeout(() => this.setState({ isMounted: true }), 1000);
  }

  componentWillUnmount() {
    this.setState({ isMounted: false });
  }

  render() {
    const { data } = this.props;
    const { isMounted } = this.state;

    const intro = () => (
      <Howdy style={{ transitionDelay: '20ms' }}>{data.title}</Howdy>
    );
    const fullName = () => (
      <HeroText style={{ transitionDelay: '50ms' }}>{data.name}.</HeroText>
    );
    const subtitle = () => (
      <Subtitle style={{ transitionDelay: '100ms' }}>{data.subtitle}</Subtitle>
    );
    const button = () => (
      <div style={{ transitionDelay: '200ms' }}>
        <HeroButton href={`mailto:${email}`}>Get In Touch</HeroButton>
      </div>
    );

    const items = [intro, fullName, subtitle, button];

    return (
      <HeroContainer>
        <TransitionGroup>
          {isMounted &&
            items.map((item, i) => (
              <CSSTransition key={i} classNames="fadeup" timeout={3000}>
                {item}
              </CSSTransition>
            ))}
        </TransitionGroup>
      </HeroContainer>
    );
  }
}

export default Hero;
