const Globals = {
  colors: {
    dark: '#173753',
    white: '#fff',
    grey: '#aab3be',
    blue: '#2075ff',
    highlight: 'rgba(255, 255, 255, 0.8)',
    transDark: 'rgba(10, 25, 47, 0.5)',
    shadowNavy: 'rgba(2, 12, 27, 0.9)'
  },

  fonts: {
    Raleway: 'Raleway, sans-serif',
    WorkSans: 'Work Sans, sans-serif'
  },

  fontSizes: {
    xsmall: '12px',
    smallish: '13px',
    small: '14px',
    medium: '16px',
    large: '18px',
    xlarge: '20px',
    xxlarge: '22px',
    h3: '60px'
  },

  easing: 'cubic-bezier(0.645, 0.045, 0.355, 1)',
  transition: 'all 0.25s cubic-bezier(0.645, 0.045, 0.355, 1)',

  borderRadius: '4px',
  headerHeight: '100px',
  margin: '20px',
};

export default Globals;
