import React, { Component } from 'react';
import ScrollReveal from 'scrollreveal';
import { srConfig, email } from '../meta';
import styled from 'styled-components';
import { Globals, Misc, Images, Section, Heading, Button } from '../styles';
const { colors, fontSizes, fonts } = Globals;

const ContactContainer = styled(Section)`
  text-align: center;
  max-width: 600px;
  margin: 0 auto 100px;
  a {
    ${Misc.inlineLink};
  }
`;
const ContactHeading = styled(Heading)`
  display: block;
  color: ${colors.blue};
  font-size: ${fontSizes.medium};
  font-family: ${fonts.Raleway};
  font-weight: normal;
  margin-bottom: 20px;
  justify-content: center;
  ${Images.desktop`font-size: ${fontSizes.small};`};
  &:before {
    bottom: 0;
    font-size: ${fontSizes.small};
    ${Images.desktop`font-size: ${fontSizes.smallish};`};
  }
  &:after {
    display: none;
  }
`;
const Title = styled.h4`
  margin: 0 0 20px;
  font-size: 60px;
  ${Images.desktop`font-size: 50px;`};
  ${Images.tablet`font-size: 40px;`};
`;
const EmailLink = styled(Button)`
  margin-top: 50px;
`;

class Contact extends Component {
  componentDidMount() {
    ScrollReveal().reveal(this.contact, srConfig());
  }

  render() {
    const html =
      "Although I'm not currently looking for any freelance opportunities, my inbox is always open. Whether for a potential project or just to say hi, I'll try my best to answer your email!";

    return (
      <ContactContainer id="contact" ref={el => (this.contact = el)}>
        <ContactHeading>What's Next?</ContactHeading>

        <Title>Get In Touch</Title>

        <p dangerouslySetInnerHTML={{ __html: html }} />

        <EmailLink
          href={`mailto:${email}`}
          target="_blank"
          rel="nofollow noopener noreferrer"
        >
          Say Hello
        </EmailLink>
      </ContactContainer>
    );
  }
}

export default Contact;
