import React, { Component } from 'react';
import ScrollReveal from 'scrollreveal';
import { srConfig } from '../meta';
import styled from 'styled-components';
import { Globals, Misc, Images, Section, Heading } from '../styles';
const { colors, fontSizes, fonts } = Globals;

const AboutContainer = styled(Section)`
  position: relative;
`;

const FlexContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  align-items: flex-start;
  ${Images.tablet`display: block;`};
`;
const ContentContainer = styled.div`
  width: 70%;
  max-width: 540px;
  font-size: ${fontSizes.medium};
  line-height: 1.5em;
  ${Images.tablet`width: 100%;`};
  a {
    ${Misc.inlineLink};
  }
`;

const SkillsContainer = styled.ul`
  display: grid;
  grid-template-columns: repeat(4, minmax(140px, 200px));
  line-height: 1em;
  overflow: hidden;
  margin-top: 30px;
`;
const Skill = styled.li`
  position: relative;
  margin-bottom: 10px;
  padding-left: 20px;
  font-family: ${fonts.Raleway};
  font-size: ${fontSizes.smallish};
  color: ${colors.blue};
  &:before {
    content: '▹';
    position: absolute;
    left: 0;
    color: ${colors.dark};
    font-size: ${fontSizes.small};
    line-height: 12px;
  }
`;
const PicContainer = styled.div`
  position: relative;
  width: 30%;
  max-width: 300px;
  margin-left: 60px;
  ${Images.tablet`margin: 60px auto 0;`};
  ${Images.phablet`width: 70%;`};
`;
const Avatar = styled.img`
  position: relative;
  mix-blend-mode: multiply;
  filter: grayscale(100%) contrast(1);
  border-radius: ${Globals.borderRadius};
  transition: ${Globals.transition};
`;
const AvatarContainer = styled.div`
  width: 100%;
  position: relative;
  border-radius: ${Globals.borderRadius};
  background-color: ${colors.blue};
  margin-left: -20px;
  &:hover,
  &:focus {
    background: transparent;
    &:after {
      top: 15px;
      left: 15px;
    }
    ${Avatar} {
      filter: none;
      mix-blend-mode: normal;
    }
  }
  &:before,
  &:after {
    content: '';
    display: block;
    position: absolute;
    width: 100%;
    height: 100%;
    border-radius: ${Globals.borderRadius};
    transition: ${Globals.transition};
  }
  &:before {
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: ${colors.dark};
    mix-blend-mode: screen;
  }
  &:after {
    border: 2px solid ${colors.blue};
    top: 20px;
    left: 20px;
    z-index: -1;
  }
`;

class About extends Component {
  componentDidMount() {
    ScrollReveal().reveal(this.about, srConfig());
  }

  render() {
    const { data } = this.props;
    return (
      <AboutContainer id="about" ref={el => (this.about = el)}>
        <Heading>{data.title}</Heading>
        <FlexContainer>
          <ContentContainer>
            <p dangerouslySetInnerHTML={{ __html: data.overview }} />
            <SkillsContainer>
              {data.skills &&
                data.skills.map((skill, i) => <Skill key={i}>{skill}</Skill>)}
            </SkillsContainer>
          </ContentContainer>
          <PicContainer>
            <AvatarContainer>
              <Avatar
                src={
                  'https://pbs.twimg.com/profile_images/435571929198510080/GvAZoDax_400x400.jpeg'
                }
                alt="Brady Fox Profile"
              />
            </AvatarContainer>
          </PicContainer>
        </FlexContainer>
      </AboutContainer>
    );
  }
}

export default About;
